import React, {useState} from "react";
import moment from "moment";

const SearchResults = (props) => {
  const[isClicked , setIsClicked] = useState(false);
  const clickHandler = () => {
    setIsClicked(prevIsClicked => !prevIsClicked)
  }

  return (
    <table className="tabel table-striped">
      <thead>
        <tr>
          <th>ID</th>
          <th>Title</th>
          <th>First Name</th>
          <th>Surname</th>
          <th>Email</th>
          <th>Room ID</th>
          <th>Check In Date</th>
          <th>Check Out Date</th>
          <th>Number of Nights</th>
        </tr>
      </thead>
      <tbody>
        {props.results.map((person) => {
          const startDate = moment(person.checkInDate);
          const endDate = moment(person.checkOutDate);
          const duration = moment.duration(endDate.diff(startDate));
          const daysDifference = duration.asDays();
          return (
            <tr key={person.id} onClick={clickHandler} style={{border}}>
            <td>{person.id}</td>
            <td>{person.title}</td>
            <td>{person.firstName}</td>
            <td>{person.surname}</td>
            <td>{person.email}</td>
            <td>{person.roomId}</td>
            <td>{person.checkInDate}</td>
            <td>{person.checkOutDate}</td>
            <td>{Math.round(daysDifference)}</td>
          </tr>
          )

        })}
      </tbody>
    </table>
  );
};

export default SearchResults;
