import React from "react";

const Order = (props) => {
  const [orders, setOrders] = useState(0);
  const orderOne = () => {
    setOrders((prevOrder) => prevOrder + 1);
  };
  return (
    <li>
      Pizzas: {orders}{" "}
      <RestaurantButton className="btn btn-primary" onClick={orderOne} />
    </li>
  );
};

export default Order;
