import React from "react";
import Card from "../UI/Card";
import classes from "./CustomerProfile.module.css"

const CustomerProfile = (props) => {
  return (
    <Card className={classes.customProfile}>
      <p className="text-center mb-3"><span className="text-primary font-weight-bold">More Information</span>(Click show profile)</p>
      <div className={classes.customerItems}>
      {props.customerID && <p> Customer_ID : {props.customerID}</p>}
      {props.customerEmail && <p>Customer_Email: {props.customerEmail}</p>}
      {props.customerPhone && <p> Customer_Phone : {props.customerPhone}</p>}
      {props.customerVIP && <p>Customer_VIP : true</p>}
    </div>
    </Card>

  );
};

export default CustomerProfile;
